function hamburger(){document.getElementById("line1").classList.toggle("line_1"),document.getElementById("line2").classList.toggle("line_2"),document.getElementById("line3").classList.toggle("line_3"),document.getElementById("nav").classList.toggle("in"),document.getElementById("body").classList.toggle("in")}document.getElementById("hamburger").addEventListener("click",(function(){hamburger()})),document.getElementById("nav").addEventListener("click",(function(){document.getElementById("line1").classList.remove("line_1"),document.getElementById("line2").classList.remove("line_2"),document.getElementById("line3").classList.remove("line_3"),document.getElementById("nav").classList.remove("in"),document.getElementById("body").classList.remove("in")})),AOS.init({duration:1e3,once:!0});const smoothScrollTrigger=document.querySelectorAll('a[href^="#"]');for(let e=0;e<smoothScrollTrigger.length;e++)smoothScrollTrigger[e].addEventListener("click",(t=>{t.preventDefault();let n=smoothScrollTrigger[e].getAttribute("href");const l=document.getElementById(n.replace("#","")).getBoundingClientRect().top+window.pageYOffset-50;window.scrollTo({top:l,behavior:"smooth"})}));